import { all, call, takeLatest, put } from 'redux-saga/effects'
import { firestore, getCurrentUser } from '../../firebase/firebase.utils'
import UserActionTypes from '../user/user.types'
import CartActionTypes from './cart.types'
import { clearCart } from './cart.actions'


export function* clearCartOnSignOut() {
  yield put(clearCart())
}

export function* onSignOutSuccess() {
  yield takeLatest(UserActionTypes.SIGN_OUT_SUCCESS, clearCartOnSignOut)
}

export function* addItemToUserCollection({payload}) {
  try {
    const {uid} = yield getCurrentUser()
    const userRef = yield firestore.collection('users').doc(uid)
    const itemRef = yield userRef.collection('items').doc(payload.id.toString())

    const snapshot = yield itemRef.get()
    const itemInfo = yield snapshot.data()

    const batch = yield firestore.batch()
    if (!snapshot.exists) {
      yield batch.set(itemRef, {
        items: {...payload, quantity: 1}
      })
    } else {
      yield batch.update(itemRef, {
        quantity: 2
      })
    }

    yield batch.commit()

  } catch(err) {
    console.log(err)
  }
}

export function* onAddItem() {
  yield takeLatest(CartActionTypes.ADD_ITEM, addItemToUserCollection)
}

export function* clearItemFromCart() {
  yield takeLatest(CartActionTypes.CLEA)
}



export function* cartSagas() {
  yield all([
    call(onSignOutSuccess),
    call(onAddItem)
  ])
}
