import React from 'react'
import './Chat.scss'
import { connect } from 'react-redux'
import { toggleChatModal } from '../../redux/chat/chat.actions'
import { ChatManager, TokenProvider} from '@pusher/chatkit-client'

import SendMessage from './SendMessage'
import Messages from './Messages'

import keys from '../../keys.js'


class Chat extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      messages: [],
      currentUser: {},
      joinableRooms: [],
      joinedRooms: [],
      roomId: null,
      userId: ''
    }
  }

  componentDidMount() {

    const chatManager = new ChatManager({
      instanceLocator: keys.instanceLocator,
      userId: 'daylan',
      tokenProvider: new TokenProvider({url: keys.tokenUrl})
    })

    chatManager.connect()
    .then(currentUser => {
      this.currentUser = currentUser
      this.getRooms()
    })
    .catch(err => console.log(err))

  }

  render() {
    return (
      <div className='container'>
        <div className='chat-container'>
          <div className='content'>
            <aside className='rooms'>
              Rooms
            </aside>

            <section className='texts'>
              <div className='messages'>
                <h3>Messages</h3>
              </div>
              <Messages />
              <SendMessage/>

            </section>

            <span onClick={() => this.props.toggleHidden()}>&#10005;</span>
          </div>

        </div>
      </div>

    )
  }
}

const mapDispatchToProps = dispatch => ({
  toggleHidden: () => dispatch(toggleChatModal())
})


export default connect(null, mapDispatchToProps)(Chat)
