import ShopActionTypes from './shop.types'

const INITIAL_STATE = {
  collections: null,
  isFetching: false
}

const shopReducer = (state=INITIAL_STATE, action) => {
  switch(action.type) {
    case ShopActionTypes.FETCH_COLLECTIONS_START:
      return {
        ...state,
        isFetching: true
      }

    case ShopActionTypes.UPDATE_SUCCESS:
      return {
        ...state,
        isFetching: false
      }

    default:
      return state
  }
}

export default shopReducer
