import  { createSelector } from 'reselect'

//inputselector
const selectCart = (state) => state.cart;

//outputselector
export const selectCartItems = createSelector(
  [selectCart],
  (cart) => cart.cartItems
);

export const selectCartItemsCount = createSelector(
  [selectCartItems],
  (cartItems) =>
    cartItems.reduce(
      (acc, item) =>
        acc + item.quantity,
      0
    )
)
