import React from 'react'
import { connect } from 'react-redux'

import CustomButton from '../custom-button/CustomButton.jsx'
import CartItem from '../cart-item/CartItem.jsx'

import './CartDropdown.scss'

const CartDropdown = (state) => (
  <div className='cart-dropdown'>
    <button onClick={() => console.log(this.props)}>test</button>
    <div className='cart-items'>
      {
        this.state.cartItems.map(item => item.id)
      }

    </div>
    <CustomButton>GO TO CHECKOUT</CustomButton>
  </div>
)

const mapStateToProps = ({cart: {cartItems}}) => ({
  cartItems
})

export default connect(mapStateToProps)(CartDropdown);
