import React from 'react'
import './Chat.scss'
import { ReactComponent as Chatsvg } from '../../assets/chat.svg'

class Chat extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      hidden: false
    }

  }

  toggleHidden = () => {
    this.setState({ hidden: !this.state.hidden})
  }

  render() {
    return (
      <div>
        {
          this.state.hidden ? <Chatsvg className='chat'/> :
          <div>hi</div>
        }
      </div>
    )
  }
}

export default Chat
