import React from 'react'
import './RoomList.scss'

class RoomList extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      roomId: ''
    }
  }


  render() {

    const orderedRooms = this.props.rooms.sort((a, b) => a.createdAt < b.createdAt)
    console.log(orderedRooms)

    return (
      <div className='room-container'>
        <h3 onClick={() => console.log(this.state)}>Rooms</h3>
        <ul>
          {
            this.props.rooms.map(room => {
              const active = room.id === this.state.roomId ? 'active' : ''

              return (
                <li className={'room ' + active} key={room.id}>
                  <a
                  onClick={() => {
                    this.props.subscribeToRoom(room.id)
                    this.setState({
                      roomId: room.id
                    }, () => console.log(this.state))
                  }}
                  className={'room ' + active}>#{room.name}</a>
                </li>
              )
            })
          }

        </ul>
      </div>
    )
  }
}

export default RoomList
