import React from 'react'


class SendMessage extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      text: ''
    }
  }

  inputHandler = (e) => {
    this.setState({
      text: e.target.value
    })
  }

  handleSubmit = (e) => {
    e.preventDefault()
  }


  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <input
          placeHolder='Chat!'
          value={this.state.text}
          onChange={this.inputHandler}
        />

      </form>
    )
  }
}

export default SendMessage
